import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Team = () => {
  const [team, setTeam] = useState(null); // User's current team
  const [players, setPlayers] = useState([]); // All available players
  const [playerId, setPlayerId] = useState(''); // Selected player ID for adding to the team
  const [optimalTeam, setOptimalTeam] = useState([]); // Optimal team from Flask API
  const [weeklyPlayers, setWeeklyPlayers] = useState([]); // Weekly player data from Flask API

  const [selectedPosition, setSelectedPosition] = useState(''); // Position filter
  const [sortOrder, setSortOrder] = useState('desc'); // Sorting order for total points
  const navigate = useNavigate();

  // Fetch player's current team, available players, optimal team, and weekly data
  useEffect(() => {
    axios.get('/api/team?userId=1')
      .then(response => {
        setTeam(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the team!', error);
      });

    axios.get('/api/players')
      .then(response => {
        setPlayers(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the players!', error);
      });

    axios.get('/api/players/total')
      .then(response => {
        setOptimalTeam(response.data);
      })
      .catch(error => {
        console.error('Error fetching optimal team from Flask API', error);
      });

    axios.get('/api/players/weekly/1') // Adjust gameweek as needed
      .then(response => {
        setWeeklyPlayers(response.data);
      })
      .catch(error => {
        console.error('Error fetching weekly player data from Flask API', error);
      });

  }, []);

  // Add a player to the user's team
  const handleAddPlayer = () => {
    axios.post('/api/team?userId=1', { id: playerId })
      .then(response => {
        setTeam(response.data); // Update the team state with the new team data
      })
      .catch(error => {
        console.error('There was an error adding the player to the team!', error);
      });
  };

  const handleReturn = () => {
    navigate('/dashboard');
  };

  // Filter and sort players based on position and total points
  const filteredPlayers = players
    .filter(player => selectedPosition === '' || player.position === selectedPosition) // Filter by position
    .sort((a, b) => {
      if (sortOrder === 'asc') {
        return a.totalPoints - b.totalPoints;
      } else {
        return b.totalPoints - a.totalPoints;
      }
    });

  return (
    <div>
      <h2>Your Team</h2>
      {team ? (
        <div>
          <h3>{team.name}</h3>
          <p>Budget Remaining: ${team.budget}</p>
          <ul>
            {team.players.map(player => (
              <li key={player.id}>
                <strong>{player.name}</strong> - Position: {player.position}, Team: {player.team}, Total Points: {player.points}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>You don't have a team yet.</p>
      )}

      <div>
        <h3>Filter and Add Players to Your Team</h3>

        {/* Position filter */}
        <label>
          Filter by Position: 
          <select value={selectedPosition} onChange={e => setSelectedPosition(e.target.value)}>
            <option value="">All Positions</option>
            <option value="GK">Goalkeeper</option>
            <option value="DEF">Defender</option>
            <option value="MID">Midfielder</option>
            <option value="FWD">Forward</option>
          </select>
        </label>

        {/* Sorting order */}
        <label style={{ marginLeft: '10px' }}>
          Sort by Total Points: 
          <select value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
            <option value="desc">Highest First</option>
            <option value="asc">Lowest First</option>
          </select>
        </label>

        {/* Display filtered and sorted players */}
        <ul>
          {filteredPlayers.map(player => (
            <li key={player.id}>
              <strong>{player.name}</strong> - ${player.value}M, Total Points: {player.totalPoints}, Weekly Points: {player.weeklyPoints}
              <button onClick={() => setPlayerId(player.id)}>Add to Team</button>
            </li>
          ))}
        </ul>
        <button onClick={handleAddPlayer}>Confirm Add Player</button>
      </div>

      <div>
        <h3>Optimal Team (From Flask API)</h3>
        {optimalTeam.length > 0 ? (
          <ul>
            {optimalTeam.map((player, index) => (
              <li key={index}>
                {player.name} - {player.team} (Position: {player.position}, Total Points: {player.totalPoints}, Value: ${player.value}M)
              </li>
            ))}
          </ul>
        ) : (
          <p>Loading optimal team...</p>
        )}
      </div>

      <div>
        <h3>Player Performance This Week</h3>
        {weeklyPlayers.length > 0 ? (
          <ul>
            {weeklyPlayers.map((player, index) => (
              <li key={index}>
                {player.name} - {player.team} (Weekly Points: {player.weeklyPoints}, Minutes Played: {player.minutes})
              </li>
            ))}
          </ul>
        ) : (
          <p>Loading weekly player data...</p>
        )}
      </div>

      <button
        onClick={handleReturn}
        style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px', cursor: 'pointer' }}
      >
        Return to Dashboard
      </button>
    </div>
  );
};

export default Team;
