import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Team = () => {
  const [team, setTeam] = useState(null);
  const [totalPlayers, setTotalPlayers] = useState([]); // Separate state for total players
  const [weeklyPlayers, setWeeklyPlayers] = useState([]); // Separate state for weekly players
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [playerId, setPlayerId] = useState("");
  const [optimalTeam, setOptimalTeam] = useState([]);
  const [topPlayers, setTopPlayers] = useState(null);
  const [sortCriteria, setSortCriteria] = useState("total_points");
  const [selectedPosition, setSelectedPosition] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("/api/team?userId=1")
      .then((response) => setTeam(response.data))
      .catch((error) =>
        console.error(
          "Error fetching the team:",
          error.response ? error.response.data : error.message
        )
      );

    // Fetch total player data
    axios
      .get("/api/players/total")
      .then((response) => {
        console.log("Total players fetched:", response.data);
        setTotalPlayers(response.data);
      })
      .catch((error) =>
        console.error(
          "Error fetching total players:",
          error.response ? error.response.data : error.message
        )
      );

    // Fetch weekly player data for a specific gameweek
    const gameweek = 1;
    axios
      .get(`/api/players/weekly/${gameweek}`)
      .then((response) => {
        console.log("Weekly players fetched:", response.data);
        setWeeklyPlayers(response.data);
      })
      .catch((error) =>
        console.error(
          "Error fetching weekly players:",
          error.response ? error.response.data : error.message
        )
      );

    axios
      .get("/api/team/optimal_team")
      .then((response) => setOptimalTeam(response.data))
      .catch((error) => console.error("Error fetching optimal team:", error));

    axios
      .get("/api/team/top_players")
      .then((response) => setTopPlayers(response.data))
      .catch((error) => console.error("Error fetching top players:", error));
  }, []);

  // This function is called when the user clicks "Search"
  const handleSearch = () => {
    // Use the correct player dataset (e.g., totalPlayers or weeklyPlayers)
    const result = totalPlayers
      .filter(
        (player) =>
          selectedPosition === "" ||
          player.position === parseInt(selectedPosition)
      )
      .sort((a, b) => (b[sortCriteria] || 0) - (a[sortCriteria] || 0));

    setFilteredPlayers(result); // Save filtered results
  };

  const handleAddPlayer = () => {
    axios
      .post("/api/team?userId=1", { id: playerId })
      .then((response) => {
        setTeam(response.data);
        setMessage("Player added successfully!");
      })
      .catch((error) => setMessage("Error adding player to the team."));
  };

  const handleReturn = () => {
    navigate("/dashboard");
  };

  return (
    <div>
      <h2>Your Team</h2>
      {team ? (
        <div>
          <h3>{team.name}</h3>
          <p>Budget Remaining: ${team.budget}</p>
          <ul>
            {team.players.map((player) => (
              <li key={player.id}>
                <strong>{player.name}</strong> - Position: {player.position},
                Team: {player.team}, Points: {player.points}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading team...</p>
      )}

      <div>
        <h3>Select a Player</h3>
        <label>Filter by Position: </label>
        <select
          value={selectedPosition}
          onChange={(e) => {
            console.log("Position selected:", e.target.value);
            setSelectedPosition(e.target.value);
          }}
        >
          <option value="">All Positions</option>
          <option value="1">Goalkeeper</option>
          <option value="2">Defender</option>
          <option value="3">Midfielder</option>
          <option value="4">Forward</option>
        </select>

        <label>Sort by: </label>
        <select
          value={sortCriteria}
          onChange={(e) => {
            console.log("Sort criteria selected:", e.target.value);
            setSortCriteria(e.target.value);
          }}
        >
          <option value="total_points">Total Points</option>
          <option value="value">Value</option>
          <option value="goals_scored">Goals Scored</option>
          <option value="assists">Assists</option>
        </select>

        <button onClick={handleSearch}>Search</button>

        <select
          value={playerId}
          onChange={(e) => {
            setPlayerId(e.target.value);
            console.log("Player selected:", e.target.value);
          }}
        >
          <option value="">Select a player</option>
          {filteredPlayers.length > 0 ? (
            filteredPlayers.map((player) => (
              <option key={player.id} value={player.id}>
                {player.name} - ${player.value}M (Points:{" "}
                {player.total_points || "N/A"}, Position: {player.position})
              </option>
            ))
          ) : (
            <option value="">
              No players found. Adjust the filters or click search again.
            </option>
          )}
        </select>
      </div>

      <button
        onClick={handleReturn}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          fontSize: "16px",
          cursor: "pointer",
        }}
      >
        Return to Dashboard
      </button>
    </div>
  );
};

export default Team;
