// src/reducers/authReducer.js
const initialState = {
    isAuthenticated: false,
    user: null,
    loading: false,
    error: null,
  };

  export default function (state = initialState, action) {
    switch (action.type) {
      case 'REGISTER_SUCCESS':
        return {
          ...state,
          isAuthenticated: true,
          user: action.payload,
          loading: false,
        };
      case 'REGISTER_FAIL':
        return {
          ...state,
          error: action.payload,
          loading: false,
        };
      default:
        return state;
    }
  }
