import axios from 'axios';
import { registerSuccess, registerFail, loginSuccess, loginFail } from '../reducers/authReducer';
import { useHistory } from 'react-router-dom';

axios.defaults.baseURL = 'http://localhost:8080';

export const registerUser = (userData) => async (dispatch) => {
  try {
    const res = await axios.post('/api/users/register', userData);
    dispatch(registerSuccess(res.data));
    // Redirect to login or dashboard after successful registration
    window.location.href = '/login'; // or '/dashboard'
  } catch (err) {
    dispatch(registerFail(err.response ? err.response.data : "Server error"));
  }
};

export const loginUser = (userData) => async (dispatch) => {
  try {
    const res = await axios.post('/api/users/login', userData);
    dispatch(loginSuccess(res.data));
    // Redirect to dashboard after successful login
    window.location.href = '/dashboard';
  } catch (err) {
    dispatch(loginFail(err.response ? err.response.data : "Server error"));
  }
};
